import client from './client';

export const writePost = ({title, body, tags}) =>
    client.post('http://121.137.72.141:8000/api/posts', {title, body, tags}, {withCredentials: true});
export const readPost = id => client.get(`http://121.137.72.141:8000/api/posts/${id}`, {withCredentials: true});
